import { Link } from "react-router-dom";
import style from "./Card.module.css";

function Card ({ name, species, gender, image, onClose, id }) {
  // console.log(props);
  return (
      <div className={style.container}>
          <button className={style.btnDelete} onClick={onClose}>Close</button>
          <Link to={`/detail/${id}`}>
          <button className={style.btnDelete}>More</button>
          </Link>
          <div className={style.title}>Name: {name}</div>
          <div className={style.title}>Specie: {species}</div>
          <div className={style.title}>Gender: {gender}</div>
          <img src={image} alt={name} className={style.imageContainer} />
      </div>
  )    

}


export default Card;
