import { Link } from "react-router-dom";
import style from "./Card.module.css";

function Card ({ name, species, gender, image, onClose, detailId }) {

  return (
      <div className={style.container}>
          <button className={style.btnDelete} onClick={onClose}>Close</button>
          <Link to={`/detail/${detailId}`}>
          <button className={style.btnDelete}>More</button>
          </Link>
          <div className={style.title}>Name: {character.name}</div>
          <div className={style.title}>Specie: {character.species}</div>
          <div className={style.title}>Gender: {character.gender}</div>
          <img src={character.image} alt={character.name} className={style.imageContainer} />
      </div>
  )    

}


export default Card;
