import { useSelector } from "react-redux";
import style from "./Favorites.module.css";
import { useNavigate } from "react-router-dom";

const Favorites = () => {
  const { myFavorites } = useSelector((state) => state);
  const navigate = useNavigate();
  const handleClick = () => {
    navigate("/home");
  };

  return (
    <div>
      {myFavorites.map((character) => {
        return (
          <>
            <button className={style.backHome} onClick={handleClick}>
              Back to home
            </button>
            <div className={style.favContainer}>
              <h1 className={style.infoFav}>Name: {character.name}</h1>
              <h1 className={style.infoFav}>Specie: {character.species}</h1>
              <h1 className={style.infoFav}>Gender: {character.gender}</h1>
              <img
                src={character.image}
                alt={character.name}
                className={style.imageContainer}
              />
            </div>
          </>
        );
      })}
    </div>
  );
};

export default Favorites;
