import { useState } from "react";
import style from "./SearchBar.module.css"

function SearchBar (props) {

  const {onSearch} = props;
  const [character, setCharacter] = useState();

  const handleChange = (event) => {
    setCharacter(event.target.value)

    if(!character.includes(event.target.name)) {
      setCharacter({
          ...character,
          character: [...character, event.target.name],
      })
    }
    else {
      setCharacter({
          ...character,
          character: character.filter(event => event !== event.target.name)
      })
    }
  }


  return (
    <div className={style.border}>
      <input className={style.inpt}
        type='search' 
        placeholder= "Search character" 
        onChange={handleChange}
        value={character}
      />
      <button onClick={() => onSearch(character) } className={style.btn}>Add</button>
    </div>
  )
}

export default SearchBar;
